// global.d.ts
declare module '*.jpg' {
  const jpg: string;
  export default jpg;
}

declare module '*.jpeg' {
  const jpeg: string;
  export default jpeg;
}

declare module '*.png' {
  const png: string;
  export default png;
}

declare module '*.svg' {
  const svg: string;
  export default svg;
}
